const pool = require('../../config/db');
const { encode } = require('../helper/user');
const jwt = require('jsonwebtoken');
const { generateToken } = require('../middlewares/authMiddlewares');
const bcrypt = require('bcrypt');
const utils = require('../../utils/index');

class UsersController {
    // [GET] /
    async index(req, res, next) {
        try {
            const query = 'SELECT * FROM users';
            const response = await pool.query(query);
            return res.status(200).json(response.rows);
        } catch (err) {
            console.log(err);
            return res.status(500).json('Internal Server Error');
        }
    }

    async show(req, res, next) {
        try {
            const id = parseInt(req.params.slug);
            const response = await pool.query('SELECT * FROM users WHERE id = $1', [id]);
            if (response.rows.length > 0) {
                return res.status(200).json({
                    message: 'Found user successfully',
                    code: 200,
                    body: {
                        user: response.rows[0],
                    },
                });
            } else {
                return res.status(404).json({
                    message: 'User not found',
                    code: 404,
                });
            }
        } catch (err) {
            console.log(err);
            return res.status(500).json('Internal Server Error');
        }
    }

    async getAvatar(req, res) {
        try {
            const { id } = req.body;
            const response = await pool.query('SELECT * FROM users WHERE id = $1', [id]);
            return res.status(200).json({
                message: 'Get image successfully',
                code: 200,
                body: response.rows[0].avatar,
            });
        } catch (err) {
            console.log(err);
            return res.status(500).json({
                message: 'Internal Server Error',
                code: 500,
                error: err,
            });
        }
    }

    // 201: successful, 409: User exists
    async create(req, res) {
        try {
            const { phone_number, password, name } = req.body;
            console.log(req.body);

            // Check if the user already exists
            const query = 'SELECT * FROM users WHERE phone_number = $1 AND provider = $2';
            var getUser = await pool.query(query, [phone_number, 'manual']);

            if (getUser.rows.length > 0) {
                return res.status(400).json({
                    message: 'Phone Number existed',
                    code: 400,
                    body: '',
                });
            }

            const avatar = `data:image/png;base64,`;

            // var newPassword = encode(password);
            const hashedPassword = await bcrypt.hash(password, 10);
            const id = await bcrypt.hash(phone_number, 10);
            const currentTime = utils.getCurrentTimeFormatted();

            const response = await pool.query(
                'INSERT INTO users (id, phone_number, password, name, avatar, provider, role, created_at, updated_at) VALUES ($1, $2, $3, $4, $5, $6, $7, $8, $9)',
                [id, phone_number, hashedPassword, name, avatar, 'manual', 'customer', currentTime, currentTime],
            );

            getUser = await pool.query(
                'SELECT * FROM users WHERE phone_number = $1 AND password = $2 AND provider = $3',
                [phone_number, hashedPassword, 'manual'],
            );

            return res.status(201).json({
                message: 'User created successfully',
                code: 201,
                body: {
                    accessToken: generateToken(getUser.rows[0].id, getUser.rows[0].role),
                    user: {
                        id: getUser.rows[0].id,
                        role: getUser.rows[0].role,
                        phone_number: getUser.rows[0].phone_number,
                        name: getUser.rows[0].name,
                        avatar: getUser.rows[0].avatar,
                    },
                },
            });
        } catch (err) {
            console.log(err);
            return res.status(500).json('Internal Server Error');
        }
    }

    async updateProfile(req, res) {
        try {
            let { id, name, gender, date_birth, avatar } = req.body;
            console.log(req.body);

            // Check user
            var user = await pool.query(`SELECT * FROM users WHERE id = $1`, [id]);

            if (user.rows.length === 0) {
                return res.status(400).json({
                    message: 'User not found',
                    code: 400,
                    body: '',
                });
            }

            if (!name) name = user.rows[0].name;
            if (!gender) gender = user.rows[0].gender ? user.rows[0].gender : '';
            if (!date_birth) date_birth = user.rows[0].date_birth ? user.rows[0].date_birth : '';
            if (!avatar) avatar = user.rows[0].avatar;

            const response = await pool.query(
                `UPDATE users SET name = $1, gender = $2, date_birth = $3, avatar = $4 WHERE id = $5`,
                [name, gender, date_birth, avatar, id],
            );

            return res.status(200).json({
                message: 'Updated successfully',
                code: 200,
                body: {
                    name,
                    date_birth,
                    gender,
                    avatar,
                },
            });
        } catch (err) {
            console.log(err);
            return res.status(500).json({
                message: 'Internal Server Error',
                code: 500,
                error: err,
            });
        }
    }

    async updatePhone(req, res) {
        try {
            let { id, phone_number } = req.body;
            if (!phone_number) return res.status(400).json({ message: 'Invalid phone_number' });

            // Check user
            var user = await pool.query(`SELECT * FROM users WHERE id = $1`, [id]);

            if (user.rows.length === 0) {
                return res.status(400).json({
                    message: 'User not found',
                    code: 400,
                    body: '',
                });
            }

            const response = await pool.query(`UPDATE users SET phone_number = $1 WHERE id = $2`, [phone_number, id]);

            return res.status(200).json({
                message: 'Updated successfully',
                code: 200,
                body: {
                    phone_number,
                },
            });
        } catch (err) {
            console.log(err);
            return res.status(500).json({
                message: 'Internal Server Error',
                code: 500,
                error: err,
            });
        }
    }

    async updateEmail(req, res) {
        try {
            let { id, email } = req.body;
            if (!email) return res.status(400).json({ message: 'Invalid email' });

            // Check user
            var user = await pool.query(`SELECT * FROM users WHERE id = $1`, [id]);

            if (user.rows.length === 0) {
                return res.status(400).json({
                    message: 'User not found',
                    code: 400,
                    body: '',
                });
            }

            const response = await pool.query(`UPDATE users SET email = $1 WHERE id = $2`, [email, id]);

            return res.status(200).json({
                message: 'Updated successfully',
                code: 200,
                body: {
                    email,
                },
            });
        } catch (err) {
            console.log(err);
            return res.status(500).json({
                message: 'Internal Server Error',
                code: 500,
                error: err,
            });
        }
    }

    async updatePassword(req, res) {
        try {
            let { id, oldPassword, newPassword } = req.body;
            if (!oldPassword || !newPassword)
                return res.status(400).json({ message: 'Invalid old_password or new_password' });

            // Check user
            var user = await pool.query(`SELECT * FROM users WHERE id = $1`, [id]);

            if (user.rows.length === 0) {
                return res.status(400).json({
                    message: 'User not found',
                    code: 400,
                    body: '',
                });
            }

            const oldPasswordMatch = await bcrypt.compare(oldPassword, user.rows[0].password);

            if (oldPasswordMatch) {
                const hashedPassword = await bcrypt.hash(newPassword, 10);
                const response = await pool.query(`UPDATE users SET password = $1 WHERE id = $2`, [hashedPassword, id]);

                return res.status(200).json({
                    message: 'Updated successfully',
                    code: 200,
                    body: {
                        phone_number,
                    },
                });
            } else {
                return res.status(400).json({
                    message: 'Current password is not match. Please try again',
                    code: 400,
                    body: '',
                });
            }
        } catch (err) {
            console.log(err);
            return res.status(500).json({
                message: 'Internal Server Error',
                code: 500,
                error: err,
            });
        }
    }
}

module.exports = new UsersController();
